test.sv<pre><code>module test;
  timeunit 1ns;
  timeprecision 1ps;

  bit clk;

  initial begin
    $monitor("clk = %0b", clk);
    repeat(4) begin
      #1ns;
      clk = ~clk;
    end
    $finish();
  end

endmodule
</code></pre>

